namespace Mirasvit\Rewards\Model;

use Mirasvit\Rewards\Api\Data\BalanceInterface;

class Balance extends \Magento\Framework\DataObject implements BalanceInterface
{
    /**
     * {@inheritdoc}
     */
    public function getCustomerId()
    {
        return $this->getData(self::KEY_CUSTOMER_ID);
    }

    /**
     * {@inheritdoc}
     */
    public function setCustomerId($customerId)
    {
        $this->setData(self::KEY_CUSTOMER_ID, $customerId);

        return $this;
    }

    /**
     * {@inheritdoc}
     */
    public function getAmount()
    {
        return $this->getData(self::KEY_AMOUNT);
    }

    /**
     * {@inheritdoc}
     */
    public function setAmount($amount)
    {
        $this->setData(self::KEY_AMOUNT, $amount);

        return $this;
    }
}