namespace Mirasvit\Rewards\Ui\Referral\Listing\Source;

use Magento\Framework\Data\OptionSourceInterface;

class Status implements OptionSourceInterface
{
    /**
     * @var array
     */
    protected $options;

    /**
     * Get options
     *
     * @return array
     */
    public function toOptionArray()
    {
        if ($this->options !== null) {
            return $this->options;
        }

        $data = ['sent' => 'Message Sent',
            'signup' => 'Signed Up',
            'referred_customer_order' => 'Made Order',
            'visited' => 'Visited by Referral Link',
        ];
        $options = [];
        foreach ($data as $key => $value) {
            $options[] = [
                'label' => $value,
                'value' => $key,
            ];
        }
        $this->options = $options;

        return $this->options;
    }
}
