DROP TABLE IF EXISTS `zg_cms_page_store`;
DROP TABLE IF EXISTS `zg_cms_page`;

CREATE TABLE `zg_cms_page` (
  `page_id` smallint(6) NOT NULL AUTO_INCREMENT COMMENT 'Page ID',
  `title` varchar(255) DEFAULT NULL COMMENT 'Page Title',
  `page_layout` varchar(255) DEFAULT NULL COMMENT 'Page Layout',
  `meta_keywords` text COMMENT 'Page Meta Keywords',
  `meta_description` text COMMENT 'Page Meta Description',
  `identifier` varchar(100) DEFAULT NULL COMMENT 'Page String Identifier',
  `content_heading` varchar(255) DEFAULT NULL COMMENT 'Page Content Heading',
  `content` mediumtext COMMENT 'Page Content',
  `creation_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT 'Page Creation Time',
  `update_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT 'Page Modification Time',
  `is_active` smallint(6) NOT NULL DEFAULT '1' COMMENT 'Is Page Active',
  `sort_order` smallint(6) NOT NULL DEFAULT '0' COMMENT 'Page Sort Order',
  `layout_update_xml` text COMMENT 'Page Layout Update Content',
  `custom_theme` varchar(100) DEFAULT NULL COMMENT 'Page Custom Theme',
  `custom_root_template` varchar(255) DEFAULT NULL COMMENT 'Page Custom Template',
  `custom_layout_update_xml` text COMMENT 'Page Custom Layout Update Content',
  `custom_theme_from` date DEFAULT NULL COMMENT 'Page Custom Theme Active From Date',
  `custom_theme_to` date DEFAULT NULL COMMENT 'Page Custom Theme Active To Date',
  `meta_title` varchar(255) DEFAULT NULL COMMENT 'Page Meta Title',
  PRIMARY KEY (`page_id`),
  KEY `ZG_CMS_PAGE_IDENTIFIER` (`identifier`),
  FULLTEXT KEY `FTI_2B3F6DC425D914597AB7E76AC87078A1` (`title`,`meta_keywords`,`meta_description`,`identifier`,`content`)
) ENGINE=InnoDB AUTO_INCREMENT=35 DEFAULT CHARSET=utf8 COMMENT='CMS Page Table';


INSERT INTO `zg_cms_page` VALUES (1,'404 Not Found','2columns-right','Page keywords','Page description','no-route','Whoops, our bad...','<dl>\r\n<dt>The page you requested was not found, and we have a fine guess why.</dt>\r\n<dd>\r\n<ul class=\"disc\">\r\n<li>If you typed the URL directly, please make sure the spelling is correct.</li>\r\n<li>If you clicked on a link to get here, the link is outdated.</li>\r\n</ul></dd>\r\n</dl>\r\n<dl>\r\n<dt>What can you do?</dt>\r\n<dd>Have no fear, help is near! There are many ways you can get back on track with Magento Store.</dd>\r\n<dd>\r\n<ul class=\"disc\">\r\n<li><a href=\"#\" onclick=\"history.go(-1); return false;\">Go back</a> to the previous page.</li>\r\n<li>Use the search bar at the top of the page to search for your products.</li>\r\n<li>Follow these links to get you back on track!<br /><a href=\"{{store url=\"\"}}\">Store Home</a> <span class=\"separator\">|</span> <a href=\"{{store url=\"customer/account\"}}\">My Account</a></li></ul></dd></dl>\r\n','2016-12-28 07:14:20','2016-12-28 07:14:20',1,0,NULL,NULL,NULL,NULL,NULL,NULL,NULL),(2,'Home page','1column',NULL,NULL,'home','Home Page','<p>CMS homepage content goes here.</p>\r\n','2016-12-28 07:14:20','2016-12-28 07:14:20',1,0,'<!--\n    <referenceContainer name=\"right\">\n        <action method=\"unsetChild\"><argument name=\"alias\" xsi:type=\"string\">right.reports.product.viewed</argument></action>\n        <action method=\"unsetChild\"><argument name=\"alias\" xsi:type=\"string\">right.reports.product.compared</argument></action>\n    </referenceContainer>-->',NULL,NULL,NULL,NULL,NULL,NULL),(3,'Enable Cookies','1column',NULL,NULL,'enable-cookies','What are Cookies?','<div class=\"enable-cookies cms-content\">\r\n<p>\"Cookies\" are little pieces of data we send when you visit our store. Cookies help us get to know you better and personalize your experience. Plus they help protect you and other shoppers from fraud.</p>\r\n<p style=\"margin-bottom: 20px;\">Set your browser to accept cookies so you can buy items, save items, and receive customized recommendations. Hereâ€™s how:</p>\r\n<ul>\r\n<li><a href=\"https://support.google.com/accounts/answer/61416?hl=en\" target=\"_blank\">Google Chrome</a></li>\r\n<li><a href=\"http://windows.microsoft.com/en-us/internet-explorer/delete-manage-cookies\" target=\"_blank\">Internet Explorer</a></li>\r\n<li><a href=\"http://support.apple.com/kb/PH19214\" target=\"_blank\">Safari</a></li>\r\n<li><a href=\"https://support.mozilla.org/en-US/kb/enable-and-disable-cookies-website-preferences\" target=\"_blank\">Mozilla/Firefox</a></li>\r\n</ul>\r\n</div>','2016-12-28 07:14:20','2016-12-28 07:14:20',1,0,NULL,NULL,NULL,NULL,NULL,NULL,NULL),(4,'Privacy and Cookie Policy','1column','Privacy and Cookie Policy',' This privacy policy sets out how this website (hereafter \"the Store\") uses and protects any information that you give the Store while using this website. ','privacy-policy-cookie-restriction-mode','Privacy and Cookie Policy','<div class=\"privacy-policy cms-content\">\r\n    <div class=\"message info\">\r\n        <span>\r\n            Please replace this text with you Privacy Policy.\r\n            Please add any additional cookies your website uses below (e.g. Google Analytics).\r\n        </span>\r\n    </div>\r\n    <p>\r\n        This privacy policy sets out how this website (hereafter \"the Store\") uses and protects any information that\r\n        you give the Store while using this website. The Store is committed to ensuring that your privacy is protected.\r\n        Should we ask you to provide certain information by which you can be identified when using this website, then\r\n        you can be assured that it will only be used in accordance with this privacy statement. The Store may change\r\n        this policy from time to time by updating this page. You should check this page from time to time to ensure\r\n        that you are happy with any changes.\r\n    </p>\r\n    <h2>What we collect</h2>\r\n    <p>We may collect the following information:</p>\r\n    <ul>\r\n        <li>name</li>\r\n        <li>contact information including email address</li>\r\n        <li>demographic information such as postcode, preferences and interests</li>\r\n        <li>other information relevant to customer surveys and/or offers</li>\r\n    </ul>\r\n    <p>\r\n        For the exhaustive list of cookies we collect see the <a href=\"#list\">List of cookies we collect</a> section.\r\n    </p>\r\n    <h2>What we do with the information we gather</h2>\r\n    <p>\r\n        We require this information to understand your needs and provide you with a better service,\r\n        and in particular for the following reasons:\r\n    </p>\r\n    <ul>\r\n        <li>Internal record keeping.</li>\r\n        <li>We may use the information to improve our products and services.</li>\r\n        <li>\r\n            We may periodically send promotional emails about new products, special offers or other information which we\r\n            think you may find interesting using the email address which you have provided.\r\n        </li>\r\n        <li>\r\n            From time to time, we may also use your information to contact you for market research purposes.\r\n            We may contact you by email, phone, fax or mail. We may use the information to customise the website\r\n            according to your interests.\r\n        </li>\r\n    </ul>\r\n    <h2>Security</h2>\r\n    <p>\r\n        We are committed to ensuring that your information is secure. In order to prevent unauthorised access or\r\n        disclosure, we have put in place suitable physical, electronic and managerial procedures to safeguard and\r\n        secure the information we collect online.\r\n    </p>\r\n    <h2>How we use cookies</h2>\r\n    <p>\r\n        A cookie is a small file which asks permission to be placed on your computer\'s hard drive.\r\n        Once you agree, the file is added and the cookie helps analyse web traffic or lets you know when you visit\r\n        a particular site. Cookies allow web applications to respond to you as an individual. The web application\r\n        can tailor its operations to your needs, likes and dislikes by gathering and remembering information about\r\n        your preferences.\r\n    </p>\r\n    <p>\r\n        We use traffic log cookies to identify which pages are being used. This helps us analyse data about web page\r\n        traffic and improve our website in order to tailor it to customer needs. We only use this information for\r\n        statistical analysis purposes and then the data is removed from the system.\r\n    </p>\r\n    <p>\r\n        Overall, cookies help us provide you with a better website, by enabling us to monitor which pages you find\r\n        useful and which you do not. A cookie in no way gives us access to your computer or any information about you,\r\n        other than the data you choose to share with us. You can choose to accept or decline cookies.\r\n        Most web browsers automatically accept cookies, but you can usually modify your browser setting\r\n        to decline cookies if you prefer. This may prevent you from taking full advantage of the website.\r\n    </p>\r\n    <h2>Links to other websites</h2>\r\n    <p>\r\n        Our website may contain links to other websites of interest. However, once you have used these links\r\n        to leave our site, you should note that we do not have any control over that other website.\r\n        Therefore, we cannot be responsible for the protection and privacy of any information which you provide whilst\r\n        visiting such sites and such sites are not governed by this privacy statement.\r\n        You should exercise caution and look at the privacy statement applicable to the website in question.\r\n    </p>\r\n    <h2>Controlling your personal information</h2>\r\n    <p>You may choose to restrict the collection or use of your personal information in the following ways:</p>\r\n    <ul>\r\n        <li>\r\n            whenever you are asked to fill in a form on the website, look for the box that you can click to indicate\r\n            that you do not want the information to be used by anybody for direct marketing purposes\r\n        </li>\r\n        <li>\r\n            if you have previously agreed to us using your personal information for direct marketing purposes,\r\n            you may change your mind at any time by letting us know using our Contact Us information\r\n        </li>\r\n    </ul>\r\n    <p>\r\n        We will not sell, distribute or lease your personal information to third parties unless we have your permission\r\n        or are required by law to do so. We may use your personal information to send you promotional information\r\n        about third parties which we think you may find interesting if you tell us that you wish this to happen.\r\n    </p>\r\n    <p>\r\n        You may request details of personal information which we hold about you under the Data Protection Act 1998.\r\n        A small fee will be payable. If you would like a copy of the information held on you please email us this\r\n        request using our Contact Us information.\r\n    </p>\r\n    <p>\r\n        If you believe that any information we are holding on you is incorrect or incomplete,\r\n        please write to or email us as soon as possible, at the above address.\r\n        We will promptly correct any information found to be incorrect.\r\n    </p>\r\n    <h2><a name=\"list\"></a>List of cookies we collect</h2>\r\n    <p>The table below lists the cookies we collect and what information they store.</p>\r\n    <table class=\"data-table data-table-definition-list\">\r\n        <thead>\r\n        <tr>\r\n            <th>Cookie Name</th>\r\n            <th>Cookie Description</th>\r\n        </tr>\r\n        </thead>\r\n        <tbody>\r\n            <tr>\r\n                <th>FORM_KEY</th>\r\n                <td>Stores randomly generated key used to prevent forged requests.</td>\r\n            </tr>\r\n            <tr>\r\n                <th>PHPSESSID</th>\r\n                <td>Your session ID on the server.</td>\r\n            </tr>\r\n            <tr>\r\n                <th>GUEST-VIEW</th>\r\n                <td>Allows guests to view and edit their orders.</td>\r\n            </tr>\r\n            <tr>\r\n                <th>PERSISTENT_SHOPPING_CART</th>\r\n                <td>A link to information about your cart and viewing history, if you have asked for this.</td>\r\n            </tr>\r\n            <tr>\r\n                <th>STF</th>\r\n                <td>Information on products you have emailed to friends.</td>\r\n            </tr>\r\n            <tr>\r\n                <th>STORE</th>\r\n                <td>The store view or language you have selected.</td>\r\n            </tr>\r\n            <tr>\r\n                <th>USER_ALLOWED_SAVE_COOKIE</th>\r\n                <td>Indicates whether a customer allowed to use cookies.</td>\r\n            </tr>\r\n            <tr>\r\n                <th>MAGE-CACHE-SESSID</th>\r\n                <td>Facilitates caching of content on the browser to make pages load faster.</td>\r\n            </tr>\r\n            <tr>\r\n                <th>MAGE-CACHE-STORAGE</th>\r\n                <td>Facilitates caching of content on the browser to make pages load faster.</td>\r\n            </tr>\r\n            <tr>\r\n                <th>MAGE-CACHE-STORAGE-SECTION-INVALIDATION</th>\r\n                <td>Facilitates caching of content on the browser to make pages load faster.</td>\r\n            </tr>\r\n            <tr>\r\n                <th>MAGE-CACHE-TIMEOUT</th>\r\n                <td>Facilitates caching of content on the browser to make pages load faster.</td>\r\n            </tr>\r\n            <tr>\r\n                <th>SECTION-DATA-IDS</th>\r\n                <td>Facilitates caching of content on the browser to make pages load faster.</td>\r\n            </tr>\r\n            <tr>\r\n                <th>PRIVATE_CONTENT_VERSION</th>\r\n                <td>Facilitates caching of content on the browser to make pages load faster.</td>\r\n            </tr>\r\n            <tr>\r\n                <th>X-MAGENTO-VARY</th>\r\n                <td>Facilitates caching of content on the server to make pages load faster.</td>\r\n            </tr>\r\n            <tr>\r\n                <th>MAGE-TRANSLATION-FILE-VERSION</th>\r\n                <td>Facilitates translation of content to other languages.</td>\r\n            </tr>\r\n            <tr>\r\n                <th>MAGE-TRANSLATION-STORAGE</th>\r\n                <td>Facilitates translation of content to other languages.</td>\r\n            </tr>\r\n        </tbody>\r\n    </table>\r\n</div>','2016-12-28 07:14:20','2017-02-20 13:11:50',1,0,'',NULL,NULL,NULL,NULL,NULL,'Privacy and Cookie Policy'),(5,'Zirg Home Page','1column','Zirg Club, Perfume, Fragrances, Haircare, Skincare, Cosmetics, Makeup, Fragrances to Designer Accessories, Watches, Sunglasses\r\n','ZirgÂ offers thousands of products and hundreds of brands, the best in the world of Beauty conveniently in one place.\r\n','ultimo-home-page1','','<p>{{block class=\"Jvs\\Qes\\Block\\Index\\Index\" template=\"index/top_search.phtml\"}}</p>\r\n<p></p>\r\n<div class=\"clearer\"></div>\r\n<p></p>\r\n<div class=\"grid12-3 homeside\">\r\n<div class=\"grid12-12\">\r\n<h6 class=\"block-title heading category_header grid12-12\">Other Ways to Shop</h6>\r\n<div class=\"block-content cate_list_side grid12-12\">\r\n<ul class=\"disc category_side\">\r\n<li><a href=\"fragrance-cologne/womens.html\">Women</a></li>\r\n<li><a href=\"haircare.html\">Haircare Tools</a></li>\r\n<li><a href=\"skincare.html\">Skincare Kit</a></li>\r\n<li><a href=\"/skincare/bath-body.html\">Bath &amp; Body</a></li>\r\n<li><a href=\"haircare/shampoo.html\">Shampoo</a></li>\r\n<li><a href=\"haircare/flat-irons-stylers-curlers.html\">Flat Irons, Stylers &amp; Curlers</a></li>\r\n<li><a href=\"giftset.html\">GiftSet</a></li>\r\n<li><a href=\"cosmetic.html\">Cosmetic</a></li>\r\n<li><a href=\"candles.html\">Candles</a></li>\r\n<li><a href=\"skincare-tools.html\">Skincare Tools</a></li>\r\n<li><a href=\"accessories.html\">Accessories</a></li>\r\n<li><a href=\"bathbody.html\">Bathbody</a></li>\r\n<li><a href=\"sunglasses.html\">Sunglasses</a></li>\r\n<li><a href=\"grocery.html\">Grocery</a></li>\r\n</ul>\r\n</div>\r\n<div class=\"beauty_buz\">\r\n<p><img src=\"{{media url=\"wysiwyg/z1.jpg\"}}\" alt=\"Beauty Buzz\" /></p>\r\n<div class=\"beauty_overlay\">\r\n<h2><a href=\"cosmetics.html\">The Beauty Buzz</a></h2>\r\n<p>Are you ready for fall? Fall brings out some amazing new looks in beauty and fashion. 2017 is all about being comfortable and sexy. Combining elements of nature into your wardrobe will definitely help you make a fashion inspired statement, proving you can be daring and risk taking.</p>\r\n<div class=\"more_btn\"><a title=\"The Beauty Buzz\" href=\"bags.html\" target=\"_self\"> <span class=\"more\">More &rsaquo;</span> </a></div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n<div class=\"my_product\">\r\n<div class=\"grid12-10 home_tabproduct\">\r\n<ul class=\"tabs\">\r\n<li class=\"tab-link current\" data-tab=\"tab-1\">NEW ARRIVALS</li>\r\n<li class=\"tab-link\" data-tab=\"tab-2\">BEST SELLERS</li>\r\n<li class=\"tab-link\" data-tab=\"tab-3\">EDITOR&rsquo;S PICKS</li>\r\n<li class=\"tab-link\" data-tab=\"tab-4\">SALE</li>\r\n</ul>\r\n<div id=\"tab-1\" class=\"tab-content current\">{{block class=\"Infortis\\Base\\Block\\Product\\ProductList\\Featured\" template=\"product/home_page_list.phtml\" attr=\"1\" category_id=\"118\" product_count=\"8\" breakpoints=\"[0, 1], [320, 2], [480, 3], [768, 2], [992, 2], [1200, 3]\" timeout=\"4000\" centered=\"1\" hide_button=\"\" block_name=\"NEW ARRIVALS\"}}</div>\r\n<div id=\"tab-2\" class=\"tab-content\">{{block class=\"Infortis\\Base\\Block\\Product\\ProductList\\Featured\" template=\"product/home_page_list.phtml\" attr=\"1\" category_id=\"119\" product_count=\"8\" breakpoints=\"[0, 1], [320, 2], [480, 3], [768, 2], [992, 2], [1200, 3]\" timeout=\"4000\" centered=\"1\" hide_button=\"\" block_name=\"NEW ARRIVALS\"}}</div>\r\n<div id=\"tab-3\" class=\"tab-content\">{{block class=\"Infortis\\Base\\Block\\Product\\ProductList\\Featured\" template=\"product/home_page_list.phtml\" attr=\"1\" category_id=\"120\" product_count=\"8\" breakpoints=\"[0, 1], [320, 2], [480, 3], [768, 2], [992, 2], [1200, 3]\" timeout=\"4000\" centered=\"1\" hide_button=\"\" block_name=\"NEW ARRIVALS\"}}</div>\r\n<div id=\"tab-4\" class=\"tab-content\">{{block class=\"Infortis\\Base\\Block\\Product\\ProductList\\Featured\" template=\"product/home_page_list.phtml\" attr=\"1\" category_id=\"93\" product_count=\"8\" breakpoints=\"[0, 1], [320, 2], [480, 3], [768, 2], [992, 2], [1200, 3]\" timeout=\"4000\" centered=\"1\" hide_button=\"\" block_name=\"NEW ARRIVALS\"}}</div>\r\n</div>\r\n</div>\r\n<div class=\"clearer\"></div>\r\n<div class=\"grid-container page-banners tabfootcat\">\r\n<div class=\"grid12-4 no-left-gutter banner\"><img src=\"{{media url=\"wysiwyg/infortis/7.jpg\"}}\" alt=\"Bags\" width=\"368\" height=\"305\" /></div>\r\n<div class=\"grid12-4\">\r\n<div class=\"feature feature-icon-hover indent first\"><span class=\"ic ic-char ib\" style=\"background-color: #ddd;\">1</span>\r\n<h4>Skincare</h4>\r\n<p>Your skin reflects your health. It\'s your body\'s canvas and one of its most valuable assets. It\'s also the only skin you\'ll ever get, so your daily habits mean everything.</p>\r\n</div>\r\n<div class=\"feature feature-icon-hover indent\"><span class=\"ic ic-char ib\" style=\"background-color: #ddd;\">2</span>\r\n<h4>Jewelry</h4>\r\n<p>Whether fancy or costume, nothing can add oomph to your wardrobe like jewelry. Here are our tips for making sure your bling always looks its best.</p>\r\n</div>\r\n<div class=\"feature feature-icon-hover indent last\"><span class=\"ic ic-char ib\" style=\"background-color: #ddd;\">3</span>\r\n<h4>Haircare</h4>\r\n<p>It seems like every day there\'s a new advancement in hair care and treatment, but which ones work best and are worth the cost and effort? See what experts say about the latest options for your hair.</p>\r\n</div>\r\n</div>\r\n<div class=\"grid12-4 no-right-gutter hide-below-768\">\r\n<div class=\"page-banners grid-container-spaced\">\r\n<div class=\"grid12-12 no-gutter banner fade-on-hover ipad_mobile1\"><a href=\"skincare.html\"> <img src=\"{{media url=\"wysiwyg/infortis/4.jpg\"}}\" alt=\"Skincare\" width=\"369\" height=\"145\" /> </a></div>\r\n<div class=\"grid12-6 no-left-gutter banner fade-on-hover ipad_mobile2\"><a href=\"zirg-jewelry-watches.html\"> <img src=\"{{media url=\"wysiwyg/infortis/5.jpg\"}}\" alt=\"Jewelry\" width=\"181\" height=\"146\" /> </a></div>\r\n<div class=\"grid12-6 no-right-gutter banner fade-on-hover ipad_mobile3\"><a href=\"haircare.html\"> <img src=\"{{media url=\"wysiwyg/infortis/6.jpg\"}}\" alt=\"Haircare\" width=\"181\" height=\"146\" /> </a></div>\r\n</div>\r\n</div>\r\n</div>\r\n<p><br /> <br /> <!--<span class=\"section-title\">Sample Custom Banners</span></p><div class=\"nested-container\"><div class=\"page-banners grid-container\"><div class=\"grid12-3 mobile-grid banner\"><a title=\"Replace this image with your own\" href=\"{{store direct_url=\"about-us\"}}\"><img src=\"{{media url=\"wysiwyg/infortis/8.jpg\"}}\" alt=\"Sample banner\" width=\"275\" height=\"275\" /></a></div><div class=\"grid12-3 mobile-grid banner\"><a title=\"Replace this image with your own\" href=\"{{store direct_url=\"about-us\"}}\"><img src=\"{{media url=\"wysiwyg/infortis/9.jpg\"}}\" alt=\"Sample banner\" width=\"275\" height=\"275\" /></a></div><div class=\"grid12-3 mobile-grid banner\"><a title=\"Replace this image with your own\" href=\"{{store direct_url=\"about-us\"}}\"><img src=\"{{media url=\"wysiwyg/infortis/11.jpg\"}}\" alt=\"Sampl