  CONSTRAINT `ZG_CAT_URL_REWRITE_PRD_CTGR_CTGR_ID_ZG_CAT_CTGR_ENTT_ENTT_ID` FOREIGN KEY (`category_id`) REFERENCES `zg_catalog_category_entity` (`entity_id`) ON DELETE CASCADE,
